CREATE TABLE online_pharmacy.medicine
(
    mdc_id int(11) PRIMARY KEY NOT NULL COMMENT 'medicine id' AUTO_INCREMENT,
    mdc_name varchar(45) COMMENT 'medicine name',
    mdc_description longtext COMMENT 'medicine description',
    mdc_dosage decimal(5) COMMENT 'medicine dosage',
    mdc_recipe_required tinyint(4) COMMENT 'recipe required',
    mdc_price decimal(5,2),
    mdc_available tinyint(1)
);
INSERT INTO online_pharmacy.medicine (mdc_id, mdc_name, mdc_description, mdc_dosage, mdc_recipe_required, mdc_price, mdc_available) VALUES (1, 'Калий-нормин', 'Фармакологическое действие: Средство, восполняющее дефицит калия в организме. Способствуют поддержанию необходимого внутри- и внеклеточного уровня калия. Калий - основной внутриклеточный ион, играет важную роль в регуляции различных функций организма. Участвует в поддержании внутриклеточного осмотического давления, в процессах проведения и передачи на иннервируемые органы нервного импульса, в сокращении скелетных мышц и в ряде биохимических процессов. Уменьшает возбудимость и проводимость миокарда, в высоких дозах - угнетает автоматизм. Фармакокинетика После приема внутрь легко и практически в любом количестве пассивно абсорбируется (абсорбция — 70%), т.к. его концентрация (как пищевого, так и освобожденного из лекарственной формы) выше в просвете тонкой кишки, чем в крови. Из таблеток ретард постепенно и медленно высвобождается на всем протяжении ЖКТ. В подвздошной и толстой кишке K+ выделяется в просвет кишки по принципу сопряженного обмена с Na+ и выделяется с желчью (10%). Распределение K+ в организме продолжается около 8 ч с момента приема. T1/2 в фазе абсорбции — 1.31 ч; время высвобождения из таблеток ретард — 6 ч. Особые условия С осторожностью применяют при нарушениях AV-проводимости; внутрь - при заболеваниях ЖКТ. В процессе лечения необходим контроль уровня калия в крови и ЭКГ, а при лечении дефицитных по калию состояний следует тщательно контролировать КЩР. У пациентов с хроническими заболеваниями почек или при любых заболеваниях, сопровождающихся нарушением выведения калия из организма, или при слишком быстром в/в введении калия хлорида возможно развитие гиперкалиемии, которая потенциально может привести к летальному исходу. Ранние клинические проявления гиперкалиемии (заострение зубца P, исчезновение зубца U, понижение сегмента ST и удлинение интервала QT) обычно появляются при концентрации калия в сыворотке от 7 до 8 мэкв/л. Более тяжелые симптомы (в т.ч. паралич мускулатуры и остановка сердца) развиваются при концентрации калия 9-10 мэкв/л. Следует иметь в виду, что гиперкалиемия, приводящая к летальному исходу, может развиться быстро и протекать бессимптомно. При передозировке калия хлорида вводят раствор натрия хлорида внутрь или в/в; или в/в 300-500 мл раствора декстрозы с содержанием 10-20 ЕД инсулина в 1000 мл. При необходимости проводят гемодиализ и перитонеальный диализ. Безопасность и эффективность применения калия хлорида у детей не установлены. Состав калия хлорид 1 г калия хлорид 1 г, что соответствует содержанию К+ 524.44 мг (13.3 ммоль) Вспомогательные вещества: кремния диоксид коллоидный безводный (аэросил А200) - 10 мг, поливинилбутирал (мовиталь В30Т) - 57.50 мг, цетиловый спирт - 17 мг, магния стеарат - 1 мг, тальк - 8 мг. Калий-нормин показания к применению Гипокалиемия различного генеза, в т.ч. обусловленная рвотой, диареей, гиперальдостеронизмом, полиурией при хронической почечной недостаточности, приемом некоторых лекарственных средств; аритмии, в т.ч. при гликозидной интоксикации; гипокалиемическая форма пароксизмальной миоплегии. Калий-нормин противопоказания — гиперкалиемия; — полная AV-блокада; — надпочечниковая недостаточность; — хроническая почечная недостаточность; — сопутствующая терапия калийсберегающими диуретиками; — метаболические нарушения (ацидоз, гиповолемия с гипонатриемией); — эрозивно-язвенные поражения ЖКТ; — беременность; — грудное вскармливание; — возраст до 18 лет; — повышенная чувствительность к препарату. Калий-нормин дозировка 1000 мг Калий-нормин побочные действия Возможны симптомы гиперкалиемии: парестезии в области верхних и нижних конечностей, мышечная слабость, аритмии, блокада сердца, остановка сердца, спутанность сознания. После приема внутрь: тошнота, рвота, диарея; имеются сообщения о язвенных поражениях желудка и тонкой кишки, иногда с кровотечением, перфорацией, последующим формированием стриктур. После в/в введения гиперкалиемия может проявляться преимущественно развитием нарушений деятельности сердца. Лекарственное взаимодействие Калийсберегающие диуретики, препараты калия, ингибиторы АПФ, НПВС повышают риск развития гиперкалиемии. Уменьшает побочное действие сердечных гликозидов. Усиливает действие хинидина, побочное действие дизопирамида. Условия хранения хранить при комнатной температуре 15-25 градусов беречь от детей Синонимы К-Лайт, Калий нормин, Калипоз пролонгатум, Калия хлорид, Калия хлорид для инъекций', 1000, 0, 0.99, null);
INSERT INTO online_pharmacy.medicine (mdc_id, mdc_name, mdc_description, mdc_dosage, mdc_recipe_required, mdc_price, mdc_available) VALUES (2, 'Лазолван рино, спрей нозальный', 'Фармакологическое действие: В исследованиях показано, что амброксол - активный ингредиент препарата Лазолван МАКС - увеличивает секрецию в дыхательных путях. Он усиливает продукцию легочного сурфактанта и стимулирует цилиарную активность. Эти эффекты приводят к усилению тока и транспорта слизи (мукоцилиарного клиренса). Усиление мукоцилиарного клиренса улучшает отхождение мокроты и облегчает кашель. У пациентов с хронической обструктивной болезнью легких длительная терапия препаратом Лазолван МАКС (на протяжении не менее 2 месяцев) приводила к значительному снижению числа обострений. Отмечалось достоверное уменьшение длительности обострений и числа дней антибиотикотерапии. Фармакокинетика Для всех лекарственных форм амброксола немедленного высвобождения характерна быстрая и почти полная абсорбция с линейной зависимостью от дозы в терапевтическом интервале концентраций. Максимальная концентрация в плазме (С1ШХ) при приеме одной капсулы пролонгированного действия достигается в среднем через 6,5 часов. Абсолютная биодоступность таблеток амброксола 30 мг составляет 79 %. В терапевтическом интервале концентраций связывание с белками плазмы крови составляет примерно 90%. Капсулы пролонгированного действия амброксола 75 мг показали относительную биодоступность 95% по сравнению с приемом 60 мг амброксола гидрохлорида в день (30 мг два раза в день) в виде таблеток (сравнение было сделано после проведения уподобления доз). Переход амброксола из крови в ткани при пероральном применении происходит быстро. Объем распределения составляет 552 л. Самые высокие концентрации активного компонента препарата наблюдаются в легких. Примерно 30 % принятой пероральной дозы подвергается эффекту первичного прохождения через печень. Исследования на микросомах печени человека показали, что изофермент CYP3A4 является преобладающей изоформой, ответственной за метаболизм амброксола до дибромантраниловой кислоты. Оставшаяся часть амброксола метаболизируется в печени, главным образом, путем глюкуронидации и путем частичного расщепления до дибромантраниловой кислоты (приблизительно 10 % от введенной дозы), а также небольшого количества дополнительных метаболитов. Период терминального полувыведения амброксола составляет около 10 часов. Общий клиренс находится в пределах 660 мл/мин, на почечный клиренс приходится примерно 8 % от общего клиренса. Методом радиоактивной метки было подсчитано, что после приема единоразовой дозы препарата в течение последующих 5 дней с мочой выделяется около 83% принятой дозы. Не обнаружено клинически значимого влияния возраста и пола на фармакокинетику амброксола, поэтому нет оснований для подбора дозировки по этим признакам. Особые условия Не следует комбинировать с противокашлевыми средствами, затрудняющими выведение мокроты. Раствор содержит консервант бензалкония хлорид, который при ингаляции может вызвать спазм бронхов у чувствительных пациентов с повышенной реактивностью дыхательных путей. Лазолван раствор для приема внутрь и ингаляций не рекомендуется смешивать с кромоглициевой кислотой и щелочными растворами. Повышение значения рН раствора выше 6,3 может вызвать выпадение осадка амброксола гидрохлорида или появление опалесценции. Пациентам, соблюдающим гипонатриевую диету, необходимо принимать во внимание, что Лазолван раствор для приема внутрь и ингаляций содержит 42,8 мг натрия в рекомендованной суточной дозе (12 мл) для взрослых и детей старше 12 лет. У пациентов с тяжелыми поражениями кожи - синдромом Стивенса-Джонсона или токсическим эпидермальным некролизом - в ранней фазе могут появляться температура, боль в теле, ринит, кашель и воспаление горла. При симптоматическом лечении возможно ошибочное назначение муколитических средств, таких как амброксола гидрохлорид. Имеются единичные сообщения о выявлении синдрома Стивенса-Джонсона и токсического эпидермального некролиза, совпавшие по времени с назначением препарата; однако причинно-следственная связь с приемом препарата отсутствует. При развитии вышеперечисленных синдромов рекомендуется прекратить лечение и немедленно обратиться за медицинской помощью. При нарушении функции почек Лазолван необходимо применять только по рекомендации врача. Состав 1 мл раствора содержит: активное вещество: амброксола гидрохлорид 7,5 мг Вспомогательные вещества: лимонной кислоты моногидрат 2 мг, натрия гидрофосфата дигидрат 4,35 мг, натрия хлорид 6,22 мг, бензалкония хлорид 225 мкг, вода очищенная 98,9705 г. 1 пастилка содержит: активное вещество – амброксола гидрохлорид 15 мг; вспомогательные вещества – акации камедь1) 850 мг, сорбитол2) 307,4 мг, карион 83 [сорбитол, маннитол, крахмал гидролизованный гидрогенизированный]2) 614,8 мг, мяты перечной листьев масло 10 мг, эвкалипта прутовидного листьев масло 2 мг, натрия сахаринат 1,8 мг, парафин жидкий (очищенная смесь жидких насыщенных углеводородов) 2,4 мг, вода очищенная3) 196,6 мг. 1) Указано количество со ссылкой на среднее содержание твердых веществ 85 % 2) Указано количество со ссылкой на среднее содержание твердых веществ 70 % 3) Указано количество со ссылкой на остаточное содержание влаги в пастилке после высушивания 1 таблетка содержит: активное вещество – амброксола гидрохлорид 30 мг; вспомогательные вещества – лактозы моногидрат 171 мг, крахмал кукурузный высушенный 36 мг, кремния диоксид коллоидный 1,8 мг, магния стеарат 1,2 мг. 5 мл сиропа содержит: активное вещество – амброксола гидрохлорид 15 мг; вспомогательные вещества – гиэтеллоза (гидроксиэтилцеллюлоза) 10 мг, раствор сорбитола 70% 2,5 г, глицерол 85% (глицерин 85%) 750 мг, бензойная кислота 10 мг, пропиленгликоль 150 мг, ароматизатор малиновый Д9599 17,5 мг, винная кислота 5 мг, вода очищенная 2,2925 г. 5 мл сиропа содержит: активное вещество – амброксола гидрохлорид 30 мг; вспомогательные вещества – гиэтеллоза (гидроксиэтилцеллюлоза) 10 мг, глицерол 85% (глицерин 85%) 750 мг, раствор сорбитола 70% 1,75 г, натрия сахарината дигидрат 1,5 мг, бензойная кислота 10 мг, пропиленгликоль 150 мг, ароматизатор апельсиновый 9/055600 2,5 мг, ароматизатор абрикосовый 208166/40988 5 мг, рацементол (ментол) 500 мкг, вода очищенная 2890,5 мг. в 1 мл раствора содержится: Активное вещество: трамазолина гидрохлорида 1,18 мг, что соответствует 1,265 мг трамазолина гидрохлорида моногидрата. Вспомогательные вещества: лимонной кислоты моногидрат 3,906 мг, натрия гидроксид 2,23 мг, бензалкония хлорид 202 мкг, гипромеллоза (гидроксипропилметилцеллюлоза) 506 мкг, повидон 30,36 мг, глицерол 85 % 10,12 мг, магния сульфат гептагидрат 714 мкг, магния хлорид гексагидрат 510 мкг, кальция хлорид дигидрат 164 мкг, натрия гидрокарбонат 20 мкг, натрия хлорид 2,651 мг, цинеол (эвкалиптол) 101 мкг, L-ментол (левоментол) 202 мкг, камфора рацемическая 202 мкг, вода очищенная до 1 мл. Лазолван показания к применению Отек слизистой оболочки носа, заложенность носа, вызванные острыми респираторными заболеваниями и/или поллинозом (ринит, сенная лихорадка). При синусите и среднем отите (евстахиите) для облегчения оттока содержимого придаточных пазух носа по рекомендации врача. Лазолван противопоказания — I триместр беременности; — повышенная чувствительность к амброксолу или другим компонентам препарата. С осторожностью назначать во II и III триместрах беременности, в период лактации (грудного вскармливания), у пациентов с почечной и/или печеночной недостаточностью. Лазолван дозировка 15мг/2мл 15мг/5мл 30мг 30мг/5мл 7,5 мг/мл Лазолван побочные действия Нарушения со стороны нервной системы Редко (?0,01% и < 0,1 %): головокружение, нарушения вкусовых ощущений Не часто (?0,1% и < 1 %): головная боль Частота не установлена*: сонливость, седативный эффект Психические нарушения Не часто (?0,1% и < 1 %): беспокойство Частота не установлена: галлюцинации, бессонница Нарушения со стороны сердечно-сосудистой системы Не часто (?0,1% и < 1 %): сердцебиение Частота не установлена*: аритмия, тахикардия, увеличение кровянового давления Нарушения со стороны органов дыхания, органов грудной клетки и средостения Часто (?1% и < 10 %): назальный дискомфорт Не часто (?0,1% и < 1 %): отек носа, сухость во рту, ринорея, чихание Редко (?0,01% и < 0,1 %): носовое кровотечение Нарушения со стороны желудочно-кишечного тракта Не часто (?0,1% и < 1 %): тошнота Нарушения со стороны иммунной системы Частота не установлена*: гиперчувствительность Нарушения со стороны кожи и подкожных тканей** Частота не установлена*: сыпь, зуд, отек кожи Общие и локальные нарушения Частота не установлена*: отек слизистой оболочки**, утомляемость * Нежелательные явления, связь которых с препаратом расценивалась как возможная, зарегистрированные при широком применении препарата. Частоту этих редких явлений трудно оценить. ** как симптомы повышенной чувствительности Лекарственное взаимодействие Некоторые антидепрессанты (ингибиторы МАО и трициклические антидепрессанты) и сосудосуживающие препараты при одновременном назначении могут вызвать повышение артериального давления. Комбинированный прием с трициклическими антидепрессантами может привести к развитию аритмии. Одновременный прием с антигипертензивными средствами (особенно с теми, которые влияют на симпатическую нервную систему) может привести к различным кардиоваскулярным эффектам. Передозировка Вслед за повышением артериального давления и тахикардией возможно (особенно у детей) падение артериального давления, развитие шока, рефлекторной брадикардии, понижение температуры тела. Особенно у детей интоксикация приводит к воздействиям на ЦНС с развитием судорог и комы, брадикардии, угнетения дыхания. Симптомами стимуляции ЦНС являются тревожность, возбуждение, галлюцинации и судороги. К симптомам угнетения ЦНС относят снижение температуры тела, вялость, сонливость и кому. Кроме того, возможно развитие следующих симптомов: мидриаз, миоз, повышенное потоотделение, лихорадка, бледность, цианоз губ, нарушения функций сердечно-сосудистой системы (включая остановку сердца); нарушение дыхания (включая дыхательную недостаточность, остановку дыхания); психологические нарушения. Лечение В случае назальной передозировки немедленно промойте или очистите нос. Может потребоваться симптоматическое лечение. Условия хранения хранить при комнатной температуре 15-25 градусов беречь от детей хранить в защищенном от света месте Синонимы Амброксол, Амбробене, Амброгексал, Амбросан, Амбросол, Афлеган, Бронхоксол, Капли Бронховерн, Лазолван, Медовент, Халиксол', 10, 0, 12.12, null);
INSERT INTO online_pharmacy.medicine (mdc_id, mdc_name, mdc_description, mdc_dosage, mdc_recipe_required, mdc_price, mdc_available) VALUES (3, 'Азитромицин', 'Фармакологическое действие: Азитромицин Зентива - Бактериостатический антибиотик широкого спектра действия из группы макролидов - азалидов. Обладает широким спектром противомикробного действия. Механизм действия азитромицина связан с подавлением синтеза белка микробной клетки. Связываясь с 50S субъединицей рибосом, угнетает пептидтранслоказу на стадии трансляции, подавляет синтез белка, замедляет рост и размножение бактерий. В высоких концентрациях оказывает бактерицидное действие. Обладает активностью в отношении ряда грамположительных, грамотрицательных, анаэробных, внутриклеточных и других микроорганизмов. К азитромицину чувствительны грамположительные кокки: Streptococcus pneumoniae (пенициллин-чувствительные штаммы), Streptococcus pyogenes, Staphylococcus aureus (метициллин-чувствительные штаммы); аэробные грамотрицательные бактерии: Haemophilus influenzae, Haemophilus parainfluenzae, Legionella pneumophila, Moraxella catarrhalis, Pasteurella multocida, Neisseria gonorrhoeae; некоторые анаэробные микроорганизмы: Clostridium perfringens, Fusobacterium spp., Prevotella spp., Porphyriomonas spp.; а также Chlamydia trachomatis, Chlamydia pneumoniae, Chlamydia psittaci, Mycoplasma pneumoniae, Mycoplasma hominis, Borrelia burgdorferi. Микроорганизмы с приобретенной резистентностью к азитромицину: аэробные грамположительные микроорганизмы -Streptococcus pneumoniae (пенициллин-резистентные штаммы и штаммы со средней чувствительностью к пенициллину). Микроорганизмы с природной резистентностью: аэробные грамположительные микроорганизмы - Enterococcus faecalis, Staphylococcus aureus, Staphylococcus epidermidis (метициллин-резистентные штаммы), анаэробные микроорганизмы - Bacteroides fragilis. Описаны случаи перекрестной резистентности между Streptococcus pneumoniae, Streptococcus pyogenes (бета-гемолитический стрептококк группы A), Enterococcus faecalis и Staphylococcus aureus (метициллин-резистентные штаммы) к эритромицину, азитромицину, другим макролидам и линкозамидам. Азитромицин не применялся для лечения инфекционных заболеваний, вызванных Salmonella typhi (МИК < 16 мг/л) и Shigella spp. Шкала чувствительности микроорганизмов к азитромицину (Минимальная ингибирующая концентрация (МИК) мг/л). Микроорганизмы МИК, мг/л Чувствительные Устойчивые Staphylococcus spp. ? 1 > 2 Streptococcus spp. (группы А, В, С, G) ? 0.25 > 0.5 Streptococcus pneumoniae ? 0.25 > 0.5 Haemophilus influenzae ? 0.12 > 4 Moraxella catarrhalis ? 0.25 > 0.5 Neisseria gonorrhoeae ? 0.25 > 0.5 Минимальные подавляющие концентрации (МПК) азитромицина. МПК90 (мкг/мл) Микроорганизмы МПК90?0.01 мкг/мл Haemophilus ducreyi, Mycoplasma pneumoniae МПК90 0.01-0.1 мкг/мл Actinomyces spp., Bordetella pertussis, Borrelia burgdorferi, Gardnerella vaginalis, Mobiluncus spp., Moraxella catarrhalis, Propionibacterium acnes МПК90 0.01-2 мкг/мл Bacteriodes bivius, Bordetella parapertussis, Brucella melitensis, Campylobacter jejuni, Clostridium perfringens, Fusobacterium necrophorum, Haemophilus influenzae, Haemophilus parainfluenzae, Helicobacter pylori, Chlamydia pneumoniae, Chlamydia trachomatis, Legionella pneumophila, Listeria monocytogenes, Neisseria gonorrhoeae, Neisseria meningitidis, Pasteurella haemolytica, Pasteurella multocida, Peptococcus spp., Peptostreptococcus spp., Plesiomonas shigelloides, Staphylococcus aureus, Streptococcus agalactiae, Staphylococcus epidermidis, Streptococcus pneumoniae, Streptococcus pyogenes, Streptococcus viridans, Streptococcus группы С, F, G, Ureaplasma urealyticum, Vibrio cholerae, Vibrio parahaemolyticus МПК90 2-8 мкг/мл Acinetobacter calcoaceticus, Aeromonas hydrophylia, Bacteroides fragilis, Bacteroides oralis, Clostridium difficile, Escherichia coli, Eubacterium lentum, Fusobacterium nucleatum, Salmonella enteritidis, Salmonella typhi, Shigella sonnei, Yersinia enterocolitica МПК90 >10 мкг/мл Citrobacter spp., Corynebacterium spp., Enterobacter aerogenes, Enterobacter cloacae, Enterococcus faecalis, Enterococcus faecium, Eubacterium limosum, Klebsiella pneumoniae, Klebsiella oxytoca, Mycoplasma hominis, Proteus spp., Pseudomonas aeruginosa, Serratia marcescens Фармакокинетика Азитромицин быстро всасывается из желудочно-кишечного тракта (ЖКТ), что обусловлено его устойчивостью в кислой среде и липофильностью. Быстро распределяется по всему организму, при этом в тканях достигаются высокие концентрации антибиотика. После приема внутрь 500 мг максимальная концентрация азитромицина в плазме крови (Cmax) достигается через 2,5 – 2,9 ч и составляет 0,4 мг/л. Биодоступность составляет 37,5%. Азитромицин хорошо проникает в дыхательные пути, органы и ткани урогенитального тракта (в частности, в предстательную железу), кожу и мягкие ткани. Высокая концентрация в тканях (в 10-50 раз выше, чем в плазме крови) и длительный период полувыведения (Т?) обусловлены низким связыванием азитромицина с белками плазмы крови, а также его способностью проникать в эукариотические клетки и концентрироваться в среде с низким рН, окружающей лизосомы. Это в свою очередь определяет большой кажущийся объем распределения (31,1 л/кг) и высокий плазменный клиренс. Способность азитромицина накапливаться преимущественно в лизосомах особенно важна для элиминации внутриклеточных возбудителей. Доказано, что фагоциты доставляют азитромицин в места локализации инфекции, где он высвобождается в процессе фагоцитоза. Концентрация азитромицина в очагах инфекции достоверно выше, чем в здоровых тканях (в среднем на 24-34%) и коррелирует со степенью воспалительного отека. Азитромицин сохраняется в бактерицидных концентрациях в течение 5-7 дней после приема последней дозы, что позволило разработать короткие (3-дневные и 5-дневные) курсы лечения. В печени деметилируется, образующиеся метаболиты не активны. Выведение азитромицина из плазмы крови проходит в 2 этапа: Т? составляет 14-20 ч в интервале от 8 до 24 ч после приема препарата и 41 ч – в интервале от 24 до 72 ч, что позволяет применять препарат 1 раз в сутки. Выводится препарат в основном с желчью в неизменном виде, небольшая часть выводится почками. Особые условия В случае пропуска приема одной дозы азитромицина пропущенную дозу следует принять как можно раньше, а последующие - с перерывами в 24 часа. Азитромицин не следует использовать для лечения пневмонии у пациентов, для которых невозможно проведение пероральной терапии по причине тяжести заболевания и/или имеющих следующие факторы риска: муковисцидоз, бактериемия или подозрение на нее, состояния, способные изменить реакцию организма на течение заболевания (иммунодефицит, функциональная аспления и др.), пациенты, нуждающиеся в госпитализации, пожилые или ослабленные пациенты. Азитромицин следует применять с осторожностью у пациентов с нарушениями функции печени легкой и средней тяжести из-за возможности развития фульминантного гепатита и тяжелой печеночной недостаточности. При наличии симптомов нарушения функции печени, таких как быстро нарастающая астения, желтуха, потемнение мочи, склонность к кровотечениям, печеночная энцефалопатия, терапию азитромицином следует прекратить и провести исследование функционального состояния печени. При нарушениях функции почек легкой и средней тяжести (КК более 40 мл/мин) терапию азитромицином следует проводить с осторожностью под контролем состояния функции почек. При терминальной стадии почечной недостаточности (КК менее 10 мл/мин) отмечается увеличение концентрации азитромицина в плазме крови на 33%. Возможно развитие резистентности микроорганизмов при несоблюдении рекомендованной длительности курсов терапии. Как и при применении других антибактериальных препаратов, при терапии препаратом Азитромицин следует регулярно обследовать пациентов на наличие невосприимчивых микроорганизмов и признаки развития суперинфекций, в том числе грибковых. Препарат Азитромицин не следует применять более длительными курсами, чем указано в инструкции, так как фармакокинетические свойства азитромицина позволяют рекомендовать короткий и простой режим дозирования. Необходимо помнить, что для профилактики фарингита/тонзиллита, вызванного Streptococcus pyogenes, а также для профилактики острой ревматической лихорадки, препаратом выбора обычно является пенициллин. При длительном приеме азитромицина возможно развитие псевдомембранозного колита, вызванного Clostridium difficile, как в виде легкой диареи, так и тяжелого колита. При развитии диареи на фоне приема азитромицина, а также через 2 месяца после окончания терапии следует исключить клостридиальный псевдомембранозный колит. Синдром замедленной реполяризации желудочков - синдром удлинения интервала QT -повышает риск развития аритмий, в том числе аритмии типа «пируэт» на фоне приема макролидов, а также азитромицина. Осторожность при применении азитромицина следует соблюдать у пациентов с удлинением интервала QT, получающих терапию антиаритмическими средствами классов IA, III, цизапридом, при гипокалиемии или гипомагниемии, клинически значимой брадикардии, аритмии или тяжелой сердечной недостаточности. Следует соблюдать осторожность при одновременном применении азитромицина и антипсихотических препаратов (пимозид), антидепрессантов (циталопрам), фторхинолонов (моксифлоксацин, левофлоксацин), циклоспорина, а также у пожилых пациентов. Применение азитромицина может спровоцировать развитие миастенического синдрома или вызвать обострение миастении. Влияние на способность к управлению транспортными средствами и механизмами При развитии нежелательных эффектов со стороны нервной системы и органа зрения следует соблюдать осторожность при выполнении действий, требующих повышенной концентрации внимания и быстроты психомоторных реакций. . Состав 1 таб. азитромицина дигидрат 262.026 мг, ?что соответствует содержанию азитромицина 250 мг Вспомогательные вещества: крахмал кукурузный прежелатинизированный - 42.5 мг, кроскармеллоза натрия - 9 мг, кальция г